SELECT t1.id, t1.content, t1.status, t1.cycle_type, t1.super_id, t1.description, t1.expiration_time, t1.create_time, t1.update_time, t1.delete_time, t2.content AS option_content, t2.score as option_score, t2.status as option_status, t2.description AS option_description, t2.create_time AS option_create_time, t2.update_time AS option_update_time FROM ( SELECT * from SUBJECT t0 where 1=1
limit #{page.startRow,jdbcType=INTEGER},#{page.pageSize,jdbcType=INTEGER}
) t1 LEFT JOIN `option` t2 ON t1.id = t2.subject_id ORDER BY t1.id,t2.id
